import React from "react";
import { View, Text, TextInput, Button, Image } from "react-native";
import { useState } from "react";
import customFonts from "../../styles/font"
import ContactsList from "../../components/ContactsList";
import styles from "./styles";
import simg from "../../resources/search.png"


const Contact = ({ navigation: {navigate} }) => {

    const contacts = [
        {
            'id': 1,
            'name': "John Smith",
            'phoneNumber': "8931234",
            'img': null
        },
        {
            'id': 2,
            'name': "John Smith2",
            'phoneNumber': "8931234",
            'img': null
        },
        {
            'id': 3,
            'name': "John Smith33234",
            'phoneNumber': "8931234",
            'img': null
        },
        {
            'id': 4,
            'name': "John 234214",
            'phoneNumber': "8931234",
            'img': null
        },
    ]

    // clear search input with calsel button 
    const [search, setSearch] = useState("");
    return (

        <View>
            <View style={styles.searchContainer}>
                <Text style={customFonts.h1}>Contact</Text>
            </View>
                <View>
                <Button title="Add Contact" onPress={() => navigate('Addcontact')} />
                <TextInput
                    style={styles.input}
                    onChangeText={setSearch}
                    value={search}
                    placeholder="Search"
                />
                <Button
                    onPress={() => setSearch("")}
                    title="Cancel"
                    color="#841584"
                />
                </View>
            <View>
                {/* Hear is the main view {list views} */}
                <ContactsList contactsInfo={contacts}/>
            </View>
        </View>
        
    );
}

export default Contact;
