import React, { useState, useRef } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  StatusBar,
  TouchableOpacity,
  Text,
} from "react-native";
import {
  Image,
  Center,
  Popover,
  Button,
  Input,
  FormControl,
  Box,
  NativeBaseProvider,
} from "native-base";
import PhoneInput from "react-native-phone-number-input";
import { Colors } from "react-native/Libraries/NewAppScreen";
import styles from "../../views/Contact/styles";
import {useSelector, useDispatch} from "react-redux";


const AddImage = () => {
    const initialFocusRef = React.useRef(null);
    const [ImageUrl, setImageUrlValue] = useState("");
    const [isOpen, setIsOpen] = useState(false);
    const dispatch = useDispatch();
    // get the value of type ADD_IMAGE
    // send the value of ImageUrl with redux action
    return (
      <Box w="100%" alignItems="center">
        <Popover
          initialFocusRef={initialFocusRef}
          trigger={(triggerProps) => {
            return <Button {...triggerProps} onPress={() => setIsOpen(true)}>Add Image </Button>;
          }}
          isOpen={isOpen} onClose={() => setIsOpen(!isOpen)}>
          <Popover.Content width="56">
            <Popover.Arrow />
            <Popover.Body>
              <FormControl>
              <Button>Camera</Button>
              </FormControl>
              <FormControl mt="1">
              <Button>From Gallery</Button>
              </FormControl>
              <FormControl mt="1">
                <Input 
              onChangeText={newText => setImageUrlValue(newText)}
              variant="outline" placeholder="Image URL" />
              </FormControl>
            </Popover.Body>
            <Popover.Footer>
            <Button.Group>
              <Button colorScheme="coolGray" variant="ghost"
              onPress={() => {setIsOpen(false);}}>
                Cancel
              </Button>
              <Button onPress={() => {
                // sendir gögnin í redux
                console.log(dispatch({type: "ADD_IMAGE", payload: {Image:ImageUrl}}));
                setIsOpen(false);
                }}>Save</Button>
            </Button.Group>
            </Popover.Footer>
          </Popover.Content>
        </Popover>
      </Box>
    );
  };

export default AddImage;
