import React, { useState, useRef } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  StatusBar,
  TouchableOpacity,
  Text,
} from "react-native";
import {
  Image,
  Center,
  Popover,
  Button,
  Input,
  FormControl,
  Box,
  NativeBaseProvider,
} from "native-base";
import PhoneInput from "react-native-phone-number-input";
import { Colors } from "react-native/Libraries/NewAppScreen";
import styles from "../../views/Contact/styles";
import AddImage from "../../components/AddImage";
import Contactimage from "../../components/ContactImage";
import PhoneNumberInput from "../../components/PhoneNumberInput";
import {useSelector, useDispatch} from "react-redux";

const Addcontact = () => {
  const dispatch = useDispatch();
  const state = useSelector((state) => state);
  const [image, setImage] = useState(null);
  const [name, setName] = useState(null);
  const [Email, setEmail] = useState(null);
  const [Phone, setPhone] = useState(null);
  const [Phonevalid, setPhonevalid] = useState(null);
  function cleardata() {
    console.log(dispatch({type: "ADD_IMAGE", payload: {Image:'https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png'}}));
  }
  function createcontact() {
    setImage(state.Image);
    setPhone(state.Phone);
    setPhonevalid(state.Phonevalid);
    // herna mundi send gogn i bakenda
    console.log('image', image ,'name',name,'email',Email,'Phone', Phone, 'Phonevalid', Phonevalid);
    // restarta redux
    }
  return (
    <View>
       <FormControl mt="3">
          <Contactimage/>
        <FormControl mt="3" >
          <AddImage />
        </FormControl>
        </FormControl>
        <FormControl mt="3" w="310" alignSelf="center">
            <Input 
            size="xl" 
            placeholder="Name" 
            backgroundColor="#FFFFFF" 
            onChangeText={(text) => {
            setName(text)}}
            />
        </FormControl>
        <FormControl mt="3" w="310" alignSelf="center">
            <Input 
            size="xl" 
            placeholder="Email" 
            backgroundColor="#FFFFFF"
            onChangeText={(text) => {
            setEmail(text)}}
            />
        </FormControl>
        <FormControl mt="3">
          <PhoneNumberInput/>
        </FormControl>
        <FormControl mt="3" w="auto" alignSelf="center">
        <Button.Group>
            <Button colorScheme="coolGray" variant="ghost"
                onPress={() => cleardata()}>
                    Clear
                  </Button>
              <Button onPress={() => createcontact()}>Create Contact</Button>
            </Button.Group>
        </FormControl>
    </View>
  );
};

export default Addcontact;
