import React, { useState, useRef } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  StatusBar,
  TouchableOpacity,
  Text,
} from "react-native";
import {
  Image,
  Center,
  Popover,
  Button,
  Input,
  FormControl,
  Box,
  NativeBaseProvider,
} from "native-base";
import PhoneInput from "react-native-phone-number-input";
import { Colors } from "react-native/Libraries/NewAppScreen";
import styles from "../../views/Contact/styles";
import {useSelector, useDispatch} from "react-redux";



const PhoneNumberInput = () => {
    // holds the value of input
    const [value, setValue] = useState("");
    // holds the value of the hole phone number
    const [formattedValue, setFormattedValue] = useState(null);
    // holds the value if phone number is valid or not
    const [valid, setValid] = useState(false);
    const phoneInput = useRef(null);
    const dispatch = useDispatch();
    console.log(formattedValue);
    const [sendt, setsendt] = useState(false);

    if (sendt == false) {
        setsendt(true);
         // sendir gögnin í redux
        console.log(dispatch({type: 'ADD_PHONE', payload: {Phone: formattedValue, Phonevalid:valid }}));
      }
    
    
  
    return (
      <Center>
        <SafeAreaView style={styles.wrapper}>
          <PhoneInput
            ref={phoneInput}
            defaultValue={value}
            defaultCode="IS"
            layout="first"
            onChangeText={(text) => {
              setValue(text);
              setsendt(false);
              const checkValid = phoneInput.current?.isValidNumber(text);
              setValid(checkValid ? checkValid : false);
            }}
            onChangeFormattedText={(text) => {
              setFormattedValue(text);
            }}
        
    
            
            withDarkTheme
            withShadow
            autoFocus
          />
        </SafeAreaView>
      </Center>
    );
    
  };

export default PhoneNumberInput;
