import React, { useState, useRef } from "react";
import {
  SafeAreaView,
  StyleSheet,
  View,
  StatusBar,
  TouchableOpacity,
  Text,
} from "react-native";
import {
  Image,
  Center,
  Popover,
  Button,
  Input,
  FormControl,
  Box,
  NativeBaseProvider,
} from "native-base";
import PhoneInput from "react-native-phone-number-input";
import { Colors } from "react-native/Libraries/NewAppScreen";
import styles from "../../views/Contact/styles";

const [Imagedb, setImagedb] = useState("");

const Contactimage = () => {
  return (
    <Center>
      <Image
        size={150}
        borderRadius={100}
        source={{
          uri: "https://wallpaperaccess.com/full/317501.jpg",
        }}
        alt="Alternate Text"
      />
    </Center>
  );
};

const PhoneNumberInput = () => {
  const [value, setValue] = useState("");
  const [formattedValue, setFormattedValue] = useState("");
  const [valid, setValid] = useState(false);
  const [showMessage, setShowMessage] = useState(false);
  const phoneInput = useRef(null);
  return (
    <Center>
      <SafeAreaView style={styles.wrapper}>
        {showMessage && (
          <View style={styles.message}>
            <Text>Value : {value}</Text>
            <Text>Formatted Value : {formattedValue}</Text>
            <Text>Valid : {valid ? "true" : "false"}</Text>
          </View>
        )}
        <PhoneInput
          ref={phoneInput}
          defaultValue={value}
          defaultCode="DM"
          layout="first"
          onChangeText={(text) => {
            setValue(text);
          }}
          onChangeFormattedText={(text) => {
            setFormattedValue(text);
          }}
          withDarkTheme
          withShadow
          autoFocus
        />
        <TouchableOpacity
          style={styles.button}
          onPress={() => {
            const checkValid = phoneInput.current?.isValidNumber(value);
            setShowMessage(true);
            setValid(checkValid ? checkValid : false);
          }}
        >
          <Text>Check</Text>
        </TouchableOpacity>
      </SafeAreaView>
    </Center>
  );
};
const AddImage = () => {
  const initialFocusRef = React.useRef(null);
  const [ImageUrl, setImageUrlValue] = useState("");
  console.log(ImageUrl);

  const onPressLearnMore = () => {
    console.log("You tapped the button!");
    setImagedb(ImageUrl);
    // close the popover after the button is pressed triggerProps.onClose();
    setIsOpen(false);

    
  };
  return (
    <Box w="100%" alignItems="center">
      <Popover
        initialFocusRef={initialFocusRef}
        trigger={(triggerProps) => {
          return <Button {...triggerProps}>Edit Info</Button>;
        }}
      >
        <Popover.Content width="56">
          <Popover.Arrow />
          <Popover.CloseButton />
          <Popover.Header>Add Image With</Popover.Header>
          <Popover.Body>
            <FormControl>
            <Button>Camera</Button>
            </FormControl>
            <FormControl mt="3">
            <Button>From Gallery</Button>
            </FormControl>
            <FormControl mt="3">
            
              <Input 
            onChangeText={newText => setImageUrlValue(newText)}
            variant="outline" placeholder="Image URL" />
            </FormControl>
          </Popover.Body>
          <Popover.Footer>
              <Button onPress={onPressLearnMore}>Save</Button>
          </Popover.Footer>
        </Popover.Content>
      </Popover>
    </Box>
  );
};

const Addcontact = () => {
  return (
    <NativeBaseProvider>
        <Contactimage />
        <AddImage />
        <PhoneNumberInput />
    </NativeBaseProvider>
  );
};

export default Addcontact;
